import React, { useState } from "react";
import ApiKeyInput from "./ApiKeyInput";
import { findGrants } from "../services/geminiService";
import type { NonprofitProfile, GrantRecommendation } from "../types";

const GrantFinder: React.FC = () => {
  const [profile, setProfile] = useState<NonprofitProfile | null>(null);
  const [grants, setGrants] = useState<GrantRecommendation[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleFindGrants = async () => {
    if (!profile) {
      setError("Please build a nonprofit profile first.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const results = await findGrants(profile);
      setGrants(results);
    } catch (err: any) {
      setError(err.message || "Failed to fetch grant recommendations.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4 space-y-4">
      <h2 className="text-xl font-bold">Grant Finder</h2>

      {/* API Key input field */}
      <ApiKeyInput />

      {/* Button to trigger grant search */}
      <button
        onClick={handleFindGrants}
        disabled={loading}
        className="bg-brand-primary text-white px-4 py-2 rounded-md hover:bg-brand-secondary"
      >
        {loading ? "Searching..." : "Find Matching Grants"}
      </button>

      {/* Error message */}
      {error && <p className="text-red-600">{error}</p>}

      {/* Results list */}
      <div className="space-y-2">
        {grants.map((grant, idx) => (
          <div key={idx} className="border p-2 rounded-md bg-slate-50">
            <h3 className="font-semibold">{grant.grantName}</h3>
            <p className="text-sm text-slate-700">{grant.description}</p>
            <a
              href={grant.website}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 underline text-sm"
            >
              {grant.funderName}
            </a>
            <p className="text-xs text-slate-500">Reason: {grant.matchReason}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GrantFinder;
