import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import GrantFinder from "./components/GrantFinder";
import ProjectWorkspace from "./components/ProjectWorkspace";
import ProfileForm from "./components/ProfileForm";
import type { NonprofitProfile, GrantProject, AppView } from "./types";

const App: React.FC = () => {
  const [profile, setProfile] = useState<NonprofitProfile | null>(null);
  const emptyProfile = {} as NonprofitProfile;

  const [projects, setProjects] = useState<GrantProject[]>([]);
  const [activeProject, setActiveProject] = useState<GrantProject | null>(null);

  // ✅ New: track which view is active
  const [view, setView] = useState<AppView>("DASHBOARD");

  // --- Handlers ---
  const handleCreateProject = (title?: string, funder?: string) => {
    const newProject: GrantProject = {
      id: crypto.randomUUID(),
      grantTitle: title || "Untitled Project",
      funder: funder || "Unknown Funder",
      status: "Draft",
      proposal: "",
      lastEdited: new Date().toISOString(),
    };
    setProjects((prev) => [...prev, newProject]);
    setView("DASHBOARD"); // go back to dashboard after creating
  };

  const handleOpenProject = (project: GrantProject) => {
    setActiveProject(project);
    setView("PROJECT");
  };

  const handleDeleteProject = (project: GrantProject) => {
    setProjects((prev) => prev.filter((p) => p.id !== project.id));
    if (activeProject?.id === project.id) {
      setActiveProject(null);
    }
    setView("DASHBOARD");
  };

  // --- Render ---
  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Nonprofit Grant Assistant</h1>

      {view === "DASHBOARD" && (
        <Dashboard
          projects={projects}
          onOpenProject={handleOpenProject}
          onDeleteProject={handleDeleteProject}
        />
      )}

      {view === "PROFILE" && (
        <ProfileForm
          profile={profile ?? emptyProfile}
          onSave={(p) => {
            setProfile(p);
            setView("DASHBOARD"); // ✅ Back to dashboard after saving
          }}
          onBack={() => setView("DASHBOARD")} // ✅ Back button works now
        />
      )}

      {view === "FINDER" && profile && (
        <GrantFinder profile={profile} onCreateProject={handleCreateProject} />
      )}

      {view === "PROJECT" && activeProject && (
        <ProjectWorkspace
          project={activeProject}
          onClose={() => setView("DASHBOARD")}
          onUpdate={(updated) => {
            setProjects((prev) =>
              prev.map((p) => (p.id === updated.id ? updated : p))
            );
            setActiveProject(updated);
          }}
        />
      )}

      {/* Simple navigation buttons */}
      <div className="mt-4 flex space-x-2">
        <button
          onClick={() => setView("DASHBOARD")}
          className="px-3 py-1 bg-slate-200 rounded-md"
        >
          Dashboard
        </button>
        <button
          onClick={() => setView("PROFILE")}
          className="px-3 py-1 bg-slate-200 rounded-md"
        >
          Profile
        </button>
        <button
          onClick={() => setView("FINDER")}
          className="px-3 py-1 bg-slate-200 rounded-md"
        >
          Grant Finder
        </button>
      </div>
    </div>
  );
};

export default App;
