import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import GrantFinder from "./components/GrantFinder";
import ProjectWorkspace from "./components/ProjectWorkspace";
import ProfileForm from "./components/ProfileForm";
import type { NonprofitProfile, GrantProject } from "./types";

const App: React.FC = () => {
  // ✅ Profile state (nullable while user builds it)
  const [profile, setProfile] = useState<NonprofitProfile | null>(null);
  // Safe default for initial render to satisfy ProfileForm's prop type
  const emptyProfile = {} as NonprofitProfile;

  // ✅ Projects state using GrantProject (matches ProjectWorkspace expectations)
  const [projects, setProjects] = useState<GrantProject[]>([]);

  // ✅ Active project (matches ProjectWorkspace)
  const [activeProject, setActiveProject] = useState<GrantProject | null>(null);

  // --- Handlers ---
  const handleCreateProject = (title?: string, funder?: string) => {
    const newProject: GrantProject = {
      id: crypto.randomUUID(),
      grantTitle: title || "Untitled Project",
      // Include funder if your GrantProject type has it; if not, you can omit this:
      funderName: funder || "Unknown Funder",
      status: "draft",
      proposal: "",
      lastEdited: new Date().toISOString(),
    };
    setProjects((prev) => [...prev, newProject]);
  };

  const handleOpenProject = (project: GrantProject) => {
    setActiveProject(project);
  };

  const handleDeleteProject = (project: GrantProject) => {
    setProjects((prev) => prev.filter((p) => p.id !== project.id));
    if (activeProject?.id === project.id) {
      setActiveProject(null);
    }
  };

  // --- Render ---
  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Nonprofit Grant Assistant</h1>

      {/* Profile builder */}
      <ProfileForm
        profile={profile ?? emptyProfile} // ✅ eliminates TS2322 while the profile is null
        onSave={setProfile}
        onBack={() => {}} // placeholder until you wire navigation
      />

      {/* Grant Finder only shows once a profile exists */}
      {profile && (
        <GrantFinder profile={profile} onCreateProject={handleCreateProject} />
      )}

      {/* Dashboard shows projects */}
      <Dashboard
        projects={projects}
        onOpenProject={handleOpenProject}
        onDeleteProject={handleDeleteProject}
      />

      {/* Project Workspace shows only when a project is active */}
      {activeProject && (
        <ProjectWorkspace
          project={activeProject}
          onClose={() => setActiveProject(null)}
        />
      )}
    </div>
  );
};

export default App;
