import React, { useState } from "react";
import { findGrants } from "../services/geminiService";
import type { NonprofitProfile, GrantRecommendation } from "../types";

interface GrantFinderProps {
  profile: NonprofitProfile;
  onCreateProject: (title?: string, funder?: string) => void;
}

const GrantFinder: React.FC<GrantFinderProps> = ({ profile, onCreateProject }) => {
  const [grants, setGrants] = useState<GrantRecommendation[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleFindGrants = async () => {
    setLoading(true);
    setError(null);
    try {
      const results = await findGrants(profile);
      setGrants(results.slice(0, 7)); // ✅ limit to 5–7 recommendations
    } catch (err: any) {
      setError(err.message || "Failed to fetch grant recommendations.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Grant Recommendations</h2>

      <button
        onClick={handleFindGrants}
        disabled={loading}
        className="bg-brand-primary text-white px-4 py-2 rounded-md hover:bg-brand-secondary mb-6"
      >
        {loading ? "Searching..." : "Find Grants"}
      </button>

      {error && <p className="text-red-600">{error}</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {grants.map((grant, idx) => (
          <div
            key={idx}
            className="border rounded-lg shadow-md p-6 bg-white flex flex-col justify-between"
          >
            <div>
              <h3 className="text-lg font-bold">{grant.funderName}</h3>
              <p className="text-md font-semibold">{grant.grantName}</p>
              <p className="text-sm text-slate-700 mt-2">{grant.description}</p>
              <p className="text-sm text-slate-600 mt-2">
                <strong>Reason:</strong> {grant.matchReason}
              </p>
            </div>
            <div className="flex justify-between items-center mt-4">
              <button
                onClick={() => onCreateProject(grant.grantName, grant.funderName)}
                className="bg-green-500 text-white px-3 py-1 rounded-md hover:bg-green-600"
              >
                Create Project
              </button>
              <a
                href={grant.website}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline text-sm"
              >
                Application Page
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GrantFinder;
