import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import GrantFinder from "./components/GrantFinder";
import ProjectWorkspace from "./components/ProjectWorkspace";
import ProfileForm from "./components/ProfileForm";
import type { Project, NonprofitProfile } from "./types";

const App: React.FC = () => {
  // ✅ State for nonprofit profile
  const [profile, setProfile] = useState<NonprofitProfile | null>(null);

  // ✅ State for projects
  const [projects, setProjects] = useState<Project[]>([]);

  // ✅ State for currently open project
  const [activeProject, setActiveProject] = useState<Project | null>(null);

  // --- Handlers ---
  const handleCreateProject = (title?: string, funder?: string) => {
    const newProject: Project = {
      title: title || "Untitled Project",
      funder: funder || "Unknown Funder",
    };
    setProjects([...projects, newProject]);
  };

  const handleOpenProject = (project: Project) => {
    setActiveProject(project);
  };

  const handleDeleteProject = (project: Project) => {
    setProjects(projects.filter((p) => p !== project));
    if (activeProject === project) {
      setActiveProject(null);
    }
  };

  // --- Render ---
  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Nonprofit Grant Assistant</h1>

      {/* Profile builder */}
      <ProfileForm onSave={setProfile} />

      {/* Grant Finder */}
      {profile && (
        <GrantFinder profile={profile} onCreateProject={handleCreateProject} />
      )}

      {/* Dashboard */}
      <Dashboard
        projects={projects}
        onOpenProject={handleOpenProject}
        onDeleteProject={handleDeleteProject}
      />

      {/* Project Workspace */}
      {activeProject && (
        <ProjectWorkspace project={activeProject} onClose={() => setActiveProject(null)} />
      )}
    </div>
  );
};

export default App;
