import React, { useState, useEffect } from "react";
import ApiKeyInput from "./ApiKeyInput";
import { findGrants } from "../services/geminiService";
import type { NonprofitProfile, GrantRecommendation } from "../types";

interface GrantFinderProps {
  profile: NonprofitProfile;
  onCreateProject: (title?: string, funder?: string) => void;
}

const GrantFinder: React.FC<GrantFinderProps> = ({ profile, onCreateProject }) => {
  const [grants, setGrants] = useState<GrantRecommendation[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasApiKey, setHasApiKey] = useState(false);

  // ✅ Helper function to check if an API key exists
  const checkApiKey = (): boolean => {
    const key = localStorage.getItem("gemini_api_key");
    return !!key && key.trim().length > 0;
  };

  useEffect(() => {
    setHasApiKey(checkApiKey());
  }, []);

  const handleFindGrants = async () => {
    if (!profile) {
      setError("Please build a nonprofit profile first.");
      return;
    }
    if (!hasApiKey) {
      setError("No API key set. Please enter your Gemini API key above.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const results = await findGrants(profile);
      setGrants(results);
    } catch (err: any) {
      setError(err.message || "Failed to fetch grant recommendations.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 border rounded-md bg-white shadow-md space-y-4">
      <h2 className="text-xl font-bold">Grant Finder</h2>

      {/* API Key input field with onSave callback */}
      <ApiKeyInput onSave={() => setHasApiKey(checkApiKey())} />

      {/* Info box explaining how to get a key */}
      <div className="bg-blue-100 border border-blue-400 text-blue-700 px-4 py-2 rounded-md mt-2">
        ℹ️ You’ll need a Google account to obtain a Gemini API key from{" "}
        <a
          href="https://ai.google.dev"
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-600 underline"
        >
          Google AI Studio
        </a>.
      </div>

      {/* Success banner if API key is saved */}
      {hasApiKey && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-2 rounded-md mt-2">
          ✅ API key detected. You’re ready to search for grants!
        </div>
      )}

      {/* Warning banner if no API key */}
      {!hasApiKey && (
        <div className="bg-yellow-100 border border-yellow-400 text-yellow-700 px-4 py-2 rounded-md">
          ⚠️ No API key detected. Please enter your Gemini API key above before searching for grants.
        </div>
      )}

      {/* Error message */}
      {error && <p className="text-red-600">{error}</p>}

      {/* Buttons to trigger grant search */}
      <div className="flex space-x-2">
        <button
          onClick={handleFindGrants}
          disabled={loading}
          className="bg-brand-primary text-white px-4 py-2 rounded-md hover:bg-brand-secondary"
        >
          {loading ? "Searching..." : "Initiate Grant Search"}
        </button>
        <button
          onClick={handleFindGrants}
          disabled={loading}
          className="bg-slate-200 px-4 py-2 rounded-md hover:bg-slate-300"
        >
          Find More Grants
        </button>
      </div>

      {/* Results list */}
      <div className="space-y-2">
        {grants.map((grant, idx) => (
          <div
            key={idx}
            className="border p-2 rounded-md bg-slate-50 cursor-pointer hover:bg-slate-100"
            onClick={() => onCreateProject(grant.grantName, grant.funderName)} // ✅ create project on click
          >
            <h3 className="font-semibold">{grant.grantName}</h3>
            <p className="text-sm text-slate-700">{grant.description}</p>
            <a
              href={grant.website}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 underline text-sm"
              onClick={(e) => e.stopPropagation()} // prevent triggering project creation when clicking link
            >
              {grant.funderName}
            </a>
            <p className="text-xs text-slate-500">Reason: {grant.matchReason}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GrantFinder;
