import React from "react";
import type { GrantProject } from "../types";

export interface ProjectWorkspaceProps {
  project: GrantProject;
  onClose?: () => void; // ✅ make optional for flexibility
}

const ProjectWorkspace: React.FC<ProjectWorkspaceProps> = ({ project, onClose }) => {
  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center p-4">
      <div className="bg-white w-full max-w-3xl rounded-md shadow-lg p-6">
        <div className="flex items-center justify-between">
          <h2 className="text-xl font-bold">
            {project.grantTitle} — {project.funder}
          </h2>
          {onClose && (
            <button
              onClick={onClose}
              className="text-sm px-3 py-1 rounded-md bg-slate-200 hover:bg-slate-300"
            >
              Close
            </button>
          )}
        </div>

        <div className="mt-4 space-y-3">
          <p className="text-sm">
            <span className="font-semibold">Status:</span> {project.status}
          </p>
          <p className="text-xs text-slate-500">
            <span className="font-semibold">Last edited:</span> {new Date(project.lastEdited).toLocaleString()}
          </p>

          <div className="mt-4">
            <label className="block text-sm font-semibold mb-1">Proposal</label>
            <textarea
              value={project.proposal}
              readOnly
              className="w-full border rounded-md p-2 text-sm bg-slate-50"
              rows={8}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectWorkspace;
