import React, { useState, useEffect } from "react";

interface ApiKeyInputProps {
  onSave?: () => void;  // optional callback
}

const ApiKeyInput: React.FC<ApiKeyInputProps> = ({ onSave }) => {
  const [apiKey, setApiKey] = useState("");

  useEffect(() => {
    const savedKey = localStorage.getItem("gemini_api_key");
    if (savedKey) {
      setApiKey(savedKey);
    }
  }, []);

  const handleSave = () => {
    if (apiKey.trim()) {
      localStorage.setItem("gemini_api_key", apiKey.trim());
      alert("API Key saved successfully!");
      if (onSave) onSave();   // ✅ notify parent
    } else {
      alert("Please enter a valid API Key.");
    }
  };

  return (
    <div className="flex items-center space-x-2 p-2 bg-slate-100 rounded-md">
      <input
        type="text"
        placeholder="Enter Gemini API Key"
        value={apiKey}
        onChange={(e) => setApiKey(e.target.value)}
        className="flex-1 rounded-md border border-slate-300 px-2 py-1 text-sm"
      />
      <button
        onClick={handleSave}
        className="bg-brand-primary text-white px-3 py-1 rounded-md text-sm hover:bg-brand-secondary"
      >
        Save
      </button>
    </div>
  );
};

export default ApiKeyInput;
